        address trader;          // Địa chỉ người swap
        uint256 blockNumber;     // Số block
    }

    // ============ STATE VARIABLES ============

    // Mapping từ cặp token đến pool tương ứng
    mapping(address => mapping(address => Pool)) public pools;

